// Addition Node Example
// THREE and TSL (Three.js Shading Language) namespaces are available!
// Enjoy! :)

// layout must be the first variable.

layout = {
    name: "3D Model",
    width: 300,
	outputType: 'Object3D'
};

// main code must be in the output function.

const group = local.get( 'fbx', () => new THREE.Group() );

async function init() {

    const { FBXLoader } = await import( 'three/addons/loaders/FBXLoader.js' );

    const loaderFBX = new FBXLoader();
    const fbx = await loaderFBX.loadAsync( 'models/fbx/stanford-bunny.fbx' );

    const model = fbx.children[ 0 ];
    model.position.set( 0, 0, .1 );
    model.scale.setScalar( .01 );

    group.add( model );

}

output = () => {

    console.log( '3D Model', group.getObjectByName( 'Stanford_Bunny' ) );

	return group;

};

