        { name: 'C', inputType: 'Object3D' }
    ]
};

function output() {

    const group = parameters[ 'C' ];
    const mesh = group.getObjectByName( 'Stanford_Bunny' );

    console.log( 'Material', mesh );

    return mesh.material;

}
