// Addition Node Example
// THREE and TSL (Three.js Shading Language) namespaces are available!
// Enjoy! :)

// layout must be the first variable.

layout = {
    name: "3D Model",
    width: 300,
	outputType: 'Object3D',
    elements: [
        { name: 'File', inputType: 'URL' }
    ]
};

// main code must be in the output function.

function loadFile() {

    const url = parameters.get( 'File' ).value;
    const fileScene = new THREE.Group();
console.log( url );
    async function load() {

        const { GLTFLoader } = await import( 'three/addons/loaders/GLTFLoader.js' );

        const loader = new GLTFLoader();
        const glTF = await loader.loadAsync( url );

        const model = glTF.scene.children[ 0 ];

        fileScene.add( model );

        console.log( "MODEL LOADED" );

    }

    load();

    return fileScene;

}

function output() {

    const url = parameters.get( 'File' );

    let group = null;

    if ( url ) {

        group = local.get( url, loadFile );

    } else {

        group = new THREE.Group();

    }

    group.scale.setScalar( 3 );

	return group;

};

