    width: 300,
	outputType: 'Material',
    elements: [
        { name: 'C', inputType: 'Object3D' },
        { name: 'Mat', inputType: 'Material' }
    ]
};

function output() {

    const group = parameters.get( 'C' );
    const material = parameters.get( 'Mat' );
    const mesh = group ? group.getObjectByName( 'Stanford_Bunny' ) : null;

   if ( mesh && material ) {

        mesh.material = material;

   }

}
